(function(){var d=document.querySelectorAll("[data-markdown]");for(var c=0,a=d.length;c<a;c++){var e=d[c];var f=e.innerHTML;var g=f.match(/^\n?(\s*)/)[1].length,b=f.match(/^\n?(\t*)/)[1].length;if(b>0){f=f.replace(new RegExp("\\n?\\t{"+b+"}","g"),"\n");}else{if(g>1){f=f.replace(new RegExp("\\n? {"+g+"}","g"),"\n");}}e.innerHTML=(new Showdown.converter()).makeHtml(f);}})();